#ifdef KEYBOARD_crkbd
oled_rotation_t oled_init_user(oled_rotation_t rotation) {
    if (!is_keyboard_master()) { return 3; } else { return 2; }
    return rotation;
}

void oled_task_user(void) {
    if (is_keyboard_master()) {
        if (timer_elapsed32(oled_timer) > 30000) {
            oled_off();
            return;
        } else {
            oled_on();
        }
        render_secondary();
    } else {
        render_main();
    }
}
#endif
